int n, m; std::cin &gt;&gt; n &gt;&gt; m; std::vector&lt;std::vector&lt;int&gt;&gt; g(n, std::vector&lt;int&gt;(m)); for(int i = 0; i &lt; n; i ++ ) { for(int j = 0; j &lt; m; j ++ ) { std::cin &gt;&gt; g[i][j]; } } std::vector&lt;std::vector&lt;int&gt;&gt; d(n, std::vector&lt;int&gt;(m, -1)); std::queue&lt;std::pair&lt;int, int&gt;&gt; q; q.push({0, 0}); d[0][0] = 0; auto ok = [&amp;](int x, int y) -&gt; bool { return !g[x][y]; }; while(!q.empty()) { auto [x, y] = q.front(); q.pop(); if(ok((x + 1) % n, y) &amp;&amp; ok((x + 2) % n, y) &amp;&amp; d[(x + 2) % n][y] == -1) { d[(x + 2) % n][y] = d[x][y] + 1; q.push({(x + 2) % n, y}); } if(ok((x + 1) % n, (y + 1) % m) &amp;&amp; d[(x + 1) % n][(y + 1) % m] == -1) { d[(x + 1) % n][(y + 1) % m] = d[x][y] + 1; q.push({(x + 1) % n, (y + 1) % m}); } } int ans = 1e9; for(int i = 0; i &lt; n; i ++ ) { if(d[i][m - 1] != -1) { int x = d[i][m - 1] % n, y = (i + 1) % n; ans = std::min(ans, d[i][m - 1] + (y - x + n) % n); } } std::cout &lt;&lt; (ans == 1e9 ? -1 : ans) &lt;&lt; &#39;
